    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_play)
        val playerView = findViewById<PlayerView>(R.id.player_view)
        playerController = DefaultPlayerController(playerView).apply {
            setPlayerListener(play = {
                Toast.makeText(
                    this@PlayActivity,
                    "Play Clicked !",
                    Toast.LENGTH_SHORT
                ).show()})
        }
        val uri = Uri.parse("android.resource://$packageName/raw/france")
        try {
            playerController.addAudioFileUri(applicationContext, uri, amplitudesGreatDB)
            //view.addAudioUrl(url,amplitudes)
        } catch (e: IOException) {
            e.printStackTrace()
        }
    }

    override fun onDestroy() {
        super.onDestroy()
        playerController.destroyPlayer()
    }
